mapper.select:
SELECT memberID
FROM yuhanmember
WHERE memberID = #{memberID}

SELECT memberClass
FROM yuhanmember
WHERE memberID = #{memberID}

SELECT memberStatus as memberGrade
FROM yuhanmember
WHERE memberID = #{memberID}

SELECT COUNT(*) FROM yuhanmember
WHERE
    memberID = #{memberID}

SELECT COUNT(*) FROM yuhan_professor
WHERE
    proID = #{proID}
